import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { editPost, singlePost } from "../actions";
import { Link, useLocation } from "react-router-dom";
import DOMPurify from "dompurify";
import Menu from "../components/Menu";

export default function BlogDetail() {
  const location = useLocation();
  const blogId = location.pathname.split("/")[2];
  console.log(blogId);
  const dispatch = useDispatch();
  const post = useSelector((state) => state?.post?.currentPost);

  const currentUser = useSelector((state) => state?.loginReducer?.currentUser);

  useEffect(() => {
    dispatch(singlePost(blogId));
  }, [blogId, dispatch]);

  return (
    <div className="container">
      <div className="single mt-3" style={{ minHeight: "calc(100vh - 204px)" }}>
        <div className="content">
          <img src={post?.postimage} alt="" />
          <div className="user">
            {post?.profilePicture && <img src={post?.profilePicture} alt="" />}
            <div className="d-flex justify-content-between">
              <div className="info">
                <span>{post?.username}</span>
                <p>Posted {post?.createdAt}</p>
              </div>
              {post?.username === currentUser?.username && (
                <div className="edit">
                  <button className="btn btn-primary">
                    <Link
                      to={`/create`}
                      onClick={() => dispatch(editPost(true))}
                    >
                      Edit
                    </Link>
                  </button>
                  <button className="btn btn-danger">Delete</button>
                </div>
              )}
            </div>
          </div>
          <h1>{post?.title}</h1>

          <p
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post?.postinfo),
            }}
          ></p>
        </div>
        <Menu cat={post?.category} />
      </div>
    </div>
  );
}
