import React, { useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getposts } from "../actions";

export default function Home() {
  const navigate = useNavigate();
  const postItems = useSelector((state) => state?.postReducer?.posts);
  const dispatch = useDispatch();
  const cat = useLocation().search;
  const postRoute = (Id) => {
    navigate(`/post/${Id}`);
  };

  useEffect(() => {
    dispatch(getposts(cat));
  }, [dispatch, cat]);

  const getText = (html) => {
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent;
  };

  return (
    <div className="container">
      <div className="home" style={{ height: "calc(100vh - 174px)" }}>
        <div className="posts">
          {postItems?.map((post) => (
            <div className="post" key={post._id}>
              <div className="img">
                <img src={post.postimage} alt="" />
              </div>
              <div className="content">
                <Link className="link" to={`/post/${post._id}`}>
                  <h1>{post.title}</h1>
                </Link>
                <p>{getText(post.postinfo.substring(0, 250))}</p>
                <button onClick={() => postRoute(post._id)}>Read More</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
