import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { singlePost } from "../actions";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function BlogDetail() {
  const location = useLocation();
  const blogId = location.pathname.split("/")[2];
  const dispatch = useDispatch();
  const post = useSelector((state) => state?.post?.currentPost);
  const currentUser = useSelector((state) => state?.loginReducer?.currentUser);
  const getText = (html) => {
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent;
  };
  useEffect(() => {
    dispatch(singlePost(blogId));
  }, [blogId, dispatch]);

  return (
    <div className="single">
      <div className="content">
        <img src={post.postimage} alt="" />
        <div className="user">
          {post.profilePicture && <img src={post.profilePicture} alt="" />}
          <div className="info">
            <span>{post.username}</span>
            <p>Posted {post.createdAt}</p>
          </div>
          {post.username === currentUser.username && (
            <div className="edit">
              <Link to={`/`}>edit</Link>
              <span>delete</span>
            </div>
          )}
        </div>
        <h1>{post.title}</h1>
        <p>{getText(post.postinfo)}</p>{" "}
      </div>
      <Menu cat={post.category} />
    </div>
  );
}
