import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { logoutUser } from "../actions/index";

export default function Pofile() {
  const currentUser = useSelector((state) => state?.loginReducer?.currentUser);
  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch(dispatch());
  };

  return (
    <section
      className="profile-section"
      style={{ height: "calc(100vh - 144px)" }}
    >
      <div className="container">
        <h1>
          Welcome{" "}
          <span style={{ textDecoration: "underline" }}>
            {currentUser.username}
          </span>
        </h1>
        <div className="user-details mt-2 p-2">
          <div className="d-flex  justify-content-between">
            <div className="profile-pic">
              <img src={currentUser.profilePicture} />
            </div>
            <div className="logout">
              <button className="btn btn-danger" onClick={logoutHandler}>
                logout
              </button>
            </div>
          </div>
          <div className="user-info">
            <h5>Name: {currentUser.username}</h5>
            <h5>Email: {currentUser.email}</h5>
            <h5>Account created at: {currentUser.createdAt}</h5>
          </div>
        </div>
      </div>
    </section>
  );
}
