import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { getposts } from "../actions";

export default function Menu({ cat }) {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const category = useSelector((state) => state?.post?.currentPost?.category);
  const diffposts = useSelector((state) => state?.diffpostsReducer?.diffposts);
  const postRoute = (Id) => {
    navigate(`/post/${Id}`);
  };
  useEffect(() => {
    dispatch(getposts(`?cat=${category}`));
  }, [dispatch]);
  const similarPosts =
    diffposts?.length > 0 ? (
      diffposts?.map((post) => (
        <div className="post" key={post?._id}>
          <img src={post?.postimage} alt="" />
          <h2>{post?.title}</h2>
          <button onClick={() => postRoute(post?._id)}>Read More</button>
        </div>
      ))
    ) : (
      <h3>No similar blog posts</h3>
    );
  return (
    <div className="menu">
      <h1>Other posts you may like</h1>
      {similarPosts}
    </div>
  );
}
